type HabitTracker struct {
	habits      []Habit      `json:"habits"`
	entries     []HabitEntry `json:"entries"`
	nextHabitID int          `json:"next_habit_id"`
	nextEntryID int          `json:"next_entry_id"`
}

func NewHabitTracker() *HabitTracker {
	return &HabitTracker{
		habits:      make([]Habit, 0),
		entries:     make([]HabitEntry, 0),
		nextHabitID: 1,
		nextEntryID: 1,
	}
}

func (ht *HabitTracker) SaveToFile(filename string) error {
	data, err := json.MarshalIndent(ht, "", "  ")
	if err != nil {
		return err
	}
	return ioutil.WriteFile(filename, data, 0644)
}

func (ht *HabitTracker) LoadFromFile(filename string) error {
	if _, err := os.Stat(filename); os.IsNotExist(err) {
		return nil // File doesn't exist, start fresh
	}

	data, err := ioutil.ReadFile(filename)
	if err != nil {
		return err
	}

	return json.Unmarshal(data, ht)
}

func (ht *HabitTracker) AddHabit(name, description, category string, frequency, difficulty int) *Habit {
	habit := Habit{
		ID:          ht.nextHabitID,
		Name:        name,
		Description: description,
		Category:    category,
		Frequency:   frequency,
		Difficulty:  difficulty,
		CreatedAt:   time.Now(),
		Archived:    false,
		Active:      true,
	}
	ht.habits = append(ht.habits, habit)
	ht.nextHabitID++
	ht.SaveToFile("habits.json")
	return &habit
}

func (ht *HabitTracker) UpdateHabit(id int, name, description, category string, frequency, difficulty int) bool {
	for i := range ht.habits {
		if ht.habits[i].ID == id {
			ht.habits[i].Name = name
			ht.habits[i].Description = description
			ht.habits[i].Category = category
			ht.habits[i].Frequency = frequency
			ht.habits[i].Difficulty = difficulty
			ht.SaveToFile("habits.json")
			return true
		}
	}
	return false
}

func (ht *HabitTracker) DeleteHabit(id int) bool {
	for i, habit := range ht.habits {
		if habit.ID == id {
			ht.habits = append(ht.habits[:i], ht.habits[i+1:]...)
			// Also remove all entries for this habit
			newEntries := make([]HabitEntry, 0)
			for _, entry := range ht.entries {
				if entry.HabitID != id {
					newEntries = append(newEntries, entry)
				}
			}
			ht.entries = newEntries
			ht.SaveToFile("habits.json")
			return true
		}
	}
	return false
}

func (ht *HabitTracker) GetActiveHabits() []Habit {
	active := make([]Habit, 0)
	for _, habit := range ht.habits {
		if !habit.Archived {
			active = append(active, habit)
		}
	}
	return active
}

func (ht *HabitTracker) GetHabitByID(id int) *Habit {
	for i := range ht.habits {
		if ht.habits[i].ID == id {
			return &ht.habits[i]
		}
	}
	return nil
}

func (ht *HabitTracker) ToggleHabitEntry(habitID int, date time.Time, notes string) {
	dateStr := date.Format("2006-01-02")

	// Find existing entry for this habit and date
	for i := range ht.entries {
		entryDate := ht.entries[i].Date.Format("2006-01-02")
		if ht.entries[i].HabitID == habitID && entryDate == dateStr {
			ht.entries[i].Done = !ht.entries[i].Done
			ht.entries[i].Notes = notes
			ht.entries[i].Timestamp = time.Now()
			ht.SaveToFile("habits.json")
			return
		}
	}

	// Create new entry if none exists
	entry := HabitEntry{
		ID:        ht.nextEntryID,
		HabitID:   habitID,
		Date:      date,
		Done:      true,
		Notes:     notes,
		Timestamp: time.Now(),
	}
	ht.entries = append(ht.entries, entry)
	ht.nextEntryID++
	ht.SaveToFile("habits.json")
}

func (ht *HabitTracker) IsHabitDoneOnDate(habitID int, date time.Time) bool {
	dateStr := date.Format("2006-01-02")

	for _, entry := range ht.entries {
		entryDate := entry.Date.Format("2006-01-02")
		if entry.HabitID == habitID && entryDate == dateStr {
			return entry.Done
		}
	}
	return false
}

func (ht *HabitTracker) GetWeeklyProgress(habitID int) (int, int) {
	now := time.Now()
	weekStart := now.AddDate(0, 0, -int(now.Weekday()))
	weekStart = time.Date(weekStart.Year(), weekStart.Month(), weekStart.Day(), 0, 0, 0, 0, weekStart.Location())

	completed := 0
	for _, entry := range ht.entries {
		if entry.HabitID == habitID && entry.Date.After(weekStart.Add(-24*time.Hour)) && entry.Date.Before(now.Add(24*time.Hour)) && entry.Done {
			completed++
		}
	}

	habit := ht.GetHabitByID(habitID)
	if habit == nil {
		return 0, 0
	}

	return completed, habit.Frequency
}

func (ht *HabitTracker) GetHabitStats(habitID int) HabitStats {
	entries := make([]HabitEntry, 0)
	for _, entry := range ht.entries {
		if entry.HabitID == habitID {
			entries = append(entries, entry)
		}
	}

	// Sort entries by date
	sort.Slice(entries, func(i, j int) bool {
		return entries[i].Date.Before(entries[j].Date)
	})

	stats := HabitStats{}

	if len(entries) == 0 {
		return stats
	}

	// Calculate streaks
	currentStreak := 0
	longestStreak := 0
	tempStreak := 0

	// Calculate success rate
	completedDays := 0
	totalDays := len(entries)

	for _, entry := range entries {
		if entry.Done {
			completedDays++
			tempStreak++
			if tempStreak > longestStreak {
				longestStreak = tempStreak
			}
		} else {
			tempStreak = 0
		}
	}

	// Current streak is the streak at the end
	if len(entries) > 0 && entries[len(entries)-1].Done {
		for i := len(entries) - 1; i >= 0; i-- {
			if entries[i].Done {
				currentStreak++
			} else {
				break
			}
		}
	}

	stats.CurrentStreak = currentStreak
	stats.LongestStreak = longestStreak
	stats.TotalDays = totalDays
	stats.CompletedDays = completedDays

	if totalDays > 0 {
		stats.SuccessRate = float64(completedDays) / float64(totalDays) * 100
	}

	return stats
}

