func getCalendarHandler(w http.ResponseWriter, r *http.Request) {
	vars := mux.Vars(r)

	// Get current month or requested month
	now := time.Now()
	year := now.Year()
	month := now.Month()

	if yearStr := vars["year"]; yearStr != "" {
		if y, err := strconv.Atoi(yearStr); err == nil {
			year = y
		}
	}

	if monthStr := vars["month"]; monthStr != "" {
		if m, err := strconv.Atoi(monthStr); err == nil && m >= 1 && m <= 12 {
			month = time.Month(m)
		}
	}

	// Generate calendar data
	firstDay := time.Date(year, month, 1, 0, 0, 0, 0, time.Local)
	lastDay := firstDay.AddDate(0, 1, -1)

	// Get all habits
	habits := tracker.GetActiveHabits()

	var calendarDays []CalendarDay

	// Add days from previous month to fill the first week
	startOfWeek := firstDay.AddDate(0, 0, -int(firstDay.Weekday()))
	for d := startOfWeek; d.Before(firstDay); d = d.AddDate(0, 0, 1) {
		habitStatus := make(map[int]bool)
		for _, habit := range habits {
			habitStatus[habit.ID] = tracker.IsHabitDoneOnDate(habit.ID, d)
		}

		calendarDays = append(calendarDays, CalendarDay{
			Day:           d.Day(),
			Date:          d.Format("2006-01-02"),
			IsCurrentMonth: false,
			IsToday:       d.Format("2006-01-02") == now.Format("2006-01-02"),
			HabitStatus:   habitStatus,
		})
	}

	// Add days of current month
	for d := firstDay; !d.After(lastDay); d = d.AddDate(0, 0, 1) {
		habitStatus := make(map[int]bool)
		for _, habit := range habits {
			habitStatus[habit.ID] = tracker.IsHabitDoneOnDate(habit.ID, d)
		}

		calendarDays = append(calendarDays, CalendarDay{
			Day:           d.Day(),
			Date:          d.Format("2006-01-02"),
			IsCurrentMonth: true,
			IsToday:       d.Format("2006-01-02") == now.Format("2006-01-02"),
			HabitStatus:   habitStatus,
		})
	}

	// Add days from next month to fill the last week
	endOfWeek := lastDay.AddDate(0, 0, 6-int(lastDay.Weekday()))
	for d := lastDay.AddDate(0, 0, 1); !d.After(endOfWeek); d = d.AddDate(0, 0, 1) {
		habitStatus := make(map[int]bool)
		for _, habit := range habits {
			habitStatus[habit.ID] = tracker.IsHabitDoneOnDate(habit.ID, d)
		}

		calendarDays = append(calendarDays, CalendarDay{
			Day:           d.Day(),
			Date:          d.Format("2006-01-02"),
			IsCurrentMonth: false,
			IsToday:       d.Format("2006-01-02") == now.Format("2006-01-02"),
			HabitStatus:   habitStatus,
		})
	}

	calendarData := CalendarData{
		CurrentMonth: firstDay.Format("January 2006"),
		Year:         year,
		Month:        int(month),
		Days:         calendarDays,
		Habits:       habits,
	}

	w.Header().Set("Content-Type", "application/json")
	json.NewEncoder(w).Encode(calendarData)
}

