	api := r.PathPrefix("/api").Subrouter()

	// Habits CRUD
	api.HandleFunc("/habits", getHabitsHandler).Methods("GET")
	api.HandleFunc("/habits", createHabitHandler).Methods("POST")
	api.HandleFunc("/habits/{id}", getHabitHandler).Methods("GET")
	api.HandleFunc("/habits/{id}", updateHabitHandler).Methods("PUT")
	api.HandleFunc("/habits/{id}", deleteHabitHandler).Methods("DELETE")

	// Habit entries
	api.HandleFunc("/habits/{id}/toggle", toggleHabitHandler).Methods("POST")
	api.HandleFunc("/habits/{id}/toggle/{date}", toggleHabitDateHandler).Methods("POST")
	api.HandleFunc("/habits/{id}/stats", getHabitStatsHandler).Methods("GET")

	// Calendar
	api.HandleFunc("/calendar", getCalendarHandler).Methods("GET")
	api.HandleFunc("/calendar/{year}/{month}", getCalendarHandler).Methods("GET")

	// Analytics
	api.HandleFunc("/analytics", getAnalyticsHandler).Methods("GET")

	// Export
	api.HandleFunc("/export", exportHandler).Methods("GET")

	fmt.Println("ðŸŽ¯ Habit Tracker API server starting on http://localhost:8080")
	fmt.Println("API endpoints available at /api/*")
	log.Fatal(http.ListenAndServe(":8080", r))
