// ParseConsoleForCosts parses Cline console output to extract API request costs
func ParseConsoleForCosts(ctx context.Context, ss *mcp.ServerSession, params *mcp.CallToolParamsFor[ParseConsoleArgs]) (*mcp.CallToolResultFor[any], error) {
	consolePath := params.Arguments.ConsolePath

	// Check if path exists
	if _, err := os.Stat(consolePath); os.IsNotExist(err) {
		return &mcp.CallToolResultFor[any]{
			Content: []mcp.Content{
				&mcp.TextContent{Text: fmt.Sprintf("Console path does not exist: %s", consolePath)},
			},
			IsError: true,
		}, nil
	}

	var costs []string
	var totalCost float64

	// If it's a directory, look for Cline task directories
	if info, err := os.Stat(consolePath); err == nil && info.IsDir() {
		// Look for Cline tasks directory structure
		tasksPath := filepath.Join(consolePath, "tasks")
		if _, err := os.Stat(tasksPath); err == nil {
			err := filepath.Walk(tasksPath, func(path string, info os.FileInfo, err error) error {
				if err != nil {
					return err
				}

				// Look for ui_messages.json files in task directories
				if info.Name() == "ui_messages.json" {
					parsedCosts, total := parseUIMessagesForCosts(path)
					costs = append(costs, parsedCosts...)
					totalCost += total
				}
				return nil
			})

			if err != nil {
				return &mcp.CallToolResultFor[any]{
					Content: []mcp.Content{
						&mcp.TextContent{Text: fmt.Sprintf("Error walking tasks directory: %v", err)},
					},
					IsError: true,
				}, nil
			}
		} else {
			// Fallback to looking for any JSON files that might contain cost data
			err := filepath.Walk(consolePath, func(path string, info os.FileInfo, err error) error {
				if err != nil {
					return err
				}

				if strings.HasSuffix(strings.ToLower(info.Name()), ".json") &&
					(strings.Contains(strings.ToLower(info.Name()), "ui_messages") ||
						strings.Contains(strings.ToLower(info.Name()), "console") ||
						strings.Contains(strings.ToLower(info.Name()), "api")) {

					parsedCosts, total := parseUIMessagesForCosts(path)
					costs = append(costs, parsedCosts...)
					totalCost += total
				}
				return nil
			})

			if err != nil {
				return &mcp.CallToolResultFor[any]{
					Content: []mcp.Content{
						&mcp.TextContent{Text: fmt.Sprintf("Error walking directory: %v", err)},
					},
					IsError: true,
				}, nil
			}
		}
	} else {
		// Single file - check if it's a JSON file
		if strings.HasSuffix(strings.ToLower(consolePath), ".json") {
			parsedCosts, total := parseUIMessagesForCosts(consolePath)
			costs = parsedCosts
			totalCost = total
		} else {
			// Fallback to text parsing for legacy console files
			costPattern := regexp.MustCompile(`API Request \$([0-9]+\.?[0-9]*)`)
			parsedCosts, total := parseFileForCosts(consolePath, costPattern)
			costs = parsedCosts
			totalCost = total
		}
	}

	result := fmt.Sprintf("Found %d API cost entries, total: $%.6f\n\nCosts found:\n%s",
		len(costs), totalCost, strings.Join(costs, "\n"))

	return &mcp.CallToolResultFor[any]{
		Content: []mcp.Content{
			&mcp.TextContent{Text: result},
		},
	}, nil
}

// parseFileForCosts parses a single file for cost information (legacy text parsing)
func parseFileForCosts(filePath string, pattern *regexp.Regexp) ([]string, float64) {
	file, err := os.Open(filePath)
	if err != nil {
		return []string{fmt.Sprintf("Error opening %s: %v", filePath, err)}, 0
	}
	defer file.Close()

	var costs []string
	var totalCost float64
	scanner := bufio.NewScanner(file)
	lineNum := 0

	for scanner.Scan() {
		lineNum++
		line := scanner.Text()

		matches := pattern.FindAllStringSubmatch(line, -1)
		for _, match := range matches {
			if len(match) > 1 {
				costStr := match[1]
				if cost, err := strconv.ParseFloat(costStr, 64); err == nil {
					costs = append(costs, fmt.Sprintf("Line %d: $%s - %s", lineNum, costStr, strings.TrimSpace(line)))
					totalCost += cost
				}
			}
		}
	}

	return costs, totalCost
}

// parseUIMessagesForCosts parses Cline's ui_messages.json files for actual API costs
func parseUIMessagesForCosts(filePath string) ([]string, float64) {
	file, err := os.Open(filePath)
	if err != nil {
		return []string{fmt.Sprintf("Error opening %s: %v", filePath, err)}, 0
	}
	defer file.Close()

	var messages []UIMessage
	decoder := json.NewDecoder(file)
	if err := decoder.Decode(&messages); err != nil {
		return []string{fmt.Sprintf("Error parsing JSON %s: %v", filePath, err)}, 0
	}

	var costs []string
	var totalCost float64
	taskID := filepath.Base(filepath.Dir(filePath))

	for _, msg := range messages {
		if msg.Type == "say" && msg.Say == "api_req_started" && msg.Text != "" {
			// Parse the JSON text content to extract cost information
			var apiData map[string]interface{}
			if err := json.Unmarshal([]byte(msg.Text), &apiData); err == nil {
				if costVal, exists := apiData["cost"]; exists {
					if cost, ok := costVal.(float64); ok && cost > 0 {
						timestamp := time.Unix(msg.TS/1000, (msg.TS%1000)*1000000).Format("2006-01-02 15:04:05")

						// Extract additional details if available
						tokensIn := 0
						tokensOut := 0
						if tokensInVal, exists := apiData["tokensIn"]; exists {
							if val, ok := tokensInVal.(float64); ok {
								tokensIn = int(val)
							}
						}
						if tokensOutVal, exists := apiData["tokensOut"]; exists {
							if val, ok := tokensOutVal.(float64); ok {
								tokensOut = int(val)
							}
						}

						costEntry := fmt.Sprintf("Task %s [%s]: $%.6f (in:%d, out:%d tokens)",
							taskID, timestamp, cost, tokensIn, tokensOut)
						costs = append(costs, costEntry)
						totalCost += cost
					}
				}
			}
		}
	}

	return costs, totalCost
}

// UpdateCostEntry updates an existing cost entry with actual cost data
func UpdateCostEntry(ctx context.Context, ss *mcp.ServerSession, params *mcp.CallToolParamsFor[UpdateCostArgs]) (*mcp.CallToolResultFor[any], error) {
	csvPath := params.Arguments.CSVPath
	requestID := params.Arguments.RequestID
	actualCost := params.Arguments.ActualCost
	costNotes := params.Arguments.CostNotes

	// Read existing CSV
	entries, err := readCostCSV(csvPath)
	if err != nil {
		return &mcp.CallToolResultFor[any]{
			Content: []mcp.Content{
				&mcp.TextContent{Text: fmt.Sprintf("Error reading CSV: %v", err)},
			},
			IsError: true,
		}, nil
	}

	// Find and update the entry
	updated := false
	for i, entry := range entries {
		if entry.RequestID == requestID {
			entries[i].RequestCost = fmt.Sprintf("%.4f", actualCost)
			if costNotes != "" {
				entries[i].CostNotes = costNotes
			} else {
				entries[i].CostNotes = "ACTUAL cost from Cline interface"
			}
			updated = true
			break
		}
	}

	if !updated {
		return &mcp.CallToolResultFor[any]{
			Content: []mcp.Content{
				&mcp.TextContent{Text: fmt.Sprintf("Request ID %s not found in CSV", requestID)},
			},
			IsError: true,
		}, nil
	}

	// Write back to CSV
	if err := writeCostCSV(csvPath, entries); err != nil {
		return &mcp.CallToolResultFor[any]{
			Content: []mcp.Content{
				&mcp.TextContent{Text: fmt.Sprintf("Error writing CSV: %v", err)},
			},
			IsError: true,
		}, nil
	}

	return &mcp.CallToolResultFor[any]{
		Content: []mcp.Content{
			&mcp.TextContent{Text: fmt.Sprintf("Successfully updated %s with actual cost $%.4f", requestID, actualCost)},
		},
	}, nil
}

// CreateCostEntry creates a new cost tracking entry
func CreateCostEntry(ctx context.Context, ss *mcp.ServerSession, params *mcp.CallToolParamsFor[CreateCostEntryArgs]) (*mcp.CallToolResultFor[any], error) {
	args := params.Arguments

	// Create new entry
	entry := CostEntry{
		RequestID:              args.RequestID,
		Phase:                  args.Phase,
		ActionDescription:      args.ActionDescription,
		ContextTokensUsed:      "0", // Will be updated when context info is available
		ContextPercentage:      "0%",
		SearchTermInTranscript: args.SearchTerm,
		RequestCost:            "0.0000",
		EstimatedTotalCostAUD:  "0.0000",
		EstimatedTotalCostUSD:  "0.0000",
		CostNotes:              "Awaiting actual cost from Cline interface",
		Timestamp:              time.Now().Format("2006-01-02 15:04:05"),
		TimeApprox:             time.Now().Format("15:04"),
	}

	if args.ActualCost > 0 {
		entry.RequestCost = fmt.Sprintf("%.4f", args.ActualCost)
		entry.CostNotes = "ACTUAL cost from Cline interface"
	}

	// Read existing entries
	entries, err := readCostCSV(args.CSVPath)
	if err != nil {
		// If file doesn't exist, create new slice
		entries = []CostEntry{}
	}

	// Add new entry
	entries = append(entries, entry)

	// Write to CSV
	if err := writeCostCSV(args.CSVPath, entries); err != nil {
		return &mcp.CallToolResultFor[any]{
			Content: []mcp.Content{
				&mcp.TextContent{Text: fmt.Sprintf("Error writing CSV: %v", err)},
			},
			IsError: true,
		}, nil
	}

	return &mcp.CallToolResultFor[any]{
		Content: []mcp.Content{
			&mcp.TextContent{Text: fmt.Sprintf("Successfully created cost entry %s", args.RequestID)},
		},
	}, nil
}

// readCostCSV reads cost entries from CSV file
func readCostCSV(filePath string) ([]CostEntry, error) {
	file, err := os.Open(filePath)
	if err != nil {
		return nil, err
	}
	defer file.Close()

	reader := csv.NewReader(file)
	records, err := reader.ReadAll()
	if err != nil {
		return nil, err
	}

	var entries []CostEntry
	// Skip header row
	for i := 1; i < len(records); i++ {
		record := records[i]
		if len(record) >= 12 {
			entries = append(entries, CostEntry{
				RequestID:              record[0],
				Phase:                  record[1],
				ActionDescription:      record[2],
				ContextTokensUsed:      record[3],
				ContextPercentage:      record[4],
				SearchTermInTranscript: record[5],
				RequestCost:            record[6],
				EstimatedTotalCostAUD:  record[7],
				EstimatedTotalCostUSD:  record[8],
				CostNotes:              record[9],
				Timestamp:              record[10],
				TimeApprox:             record[11],
			})
		}
	}

	return entries, nil
}

// writeCostCSV writes cost entries to CSV file
func writeCostCSV(filePath string, entries []CostEntry) error {
	// Create directory if it doesn't exist
	dir := filepath.Dir(filePath)
	if err := os.MkdirAll(dir, 0755); err != nil {
		return err
	}

	file, err := os.Create(filePath)
	if err != nil {
		return err
	}
	defer file.Close()

	writer := csv.NewWriter(file)
	defer writer.Flush()

	// Write header
	header := []string{
		"Request_ID", "Phase", "Action_Description", "Context_Tokens_Used",
		"Context_Percentage", "Search_Term_In_Transcript", "Request_cost",
		"Estimated_Total_Cost_AUD", "Estimated_Total_Cost_USD", "Cost_Notes",
		"Timestamp", "Time_Approx",
	}
	if err := writer.Write(header); err != nil {
		return err
	}

	// Write entries
	for _, entry := range entries {
		record := []string{
			entry.RequestID,
			entry.Phase,
			entry.ActionDescription,
			entry.ContextTokensUsed,
			entry.ContextPercentage,
			entry.SearchTermInTranscript,
			entry.RequestCost,
			entry.EstimatedTotalCostAUD,
			entry.EstimatedTotalCostUSD,
			entry.CostNotes,
			entry.Timestamp,
			entry.TimeApprox,
		}
		if err := writer.Write(record); err != nil {
			return err
		}
	}

	return nil
}

