throw new Error(\`Server "${serverName}" not found in settings\`)

}



config.mcpServers\[serverName\] \= {

...config.mcpServers\[serverName\],

timeout,

}



await fs.writeFile(settingsPath, JSON.stringify(config, null, 2))



await this.updateServerConnections(config.mcpServers)

