// detectRepositoryRoot attempts to find the repository root where Cline is working
func detectRepositoryRoot() (string, error) {
	// Try to get current working directory from environment or other hints
	// For now, we'll use a simple heuristic based on common repository markers

	// Start from user's home directory and look for common project locations
	homeDir, err := os.UserHomeDir()
	if err != nil {
		return "", fmt.Errorf("failed to get home directory: %v", err)
	}

	// Common project locations to search
	projectPaths := []string{
		filepath.Join(homeDir, "Projects"),
		filepath.Join(homeDir, "Documents"),
		filepath.Join(homeDir, "Desktop"),
		homeDir,
	}

	// Look for directories with repository markers
	for _, basePath := range projectPaths {
		if repoRoot := findRepoInPath(basePath); repoRoot != "" {
			return repoRoot, nil
		}
	}

	// Fallback: try to detect from current working directory
	cwd, err := os.Getwd()
	if err == nil {
		if repoRoot := findRepoRoot(cwd); repoRoot != "" {
			return repoRoot, nil
		}
	}

	return "", fmt.Errorf("could not detect repository root")
}

// findRepoInPath searches for repositories in the given path
func findRepoInPath(basePath string) string {
	entries, err := os.ReadDir(basePath)
	if err != nil {
		return ""
	}

	for _, entry := range entries {
		if entry.IsDir() {
			dirPath := filepath.Join(basePath, entry.Name())
			if repoRoot := findRepoRoot(dirPath); repoRoot != "" {
				return repoRoot
			}
		}
	}

	return ""
}

